mapper.select:
SELECT ar.article_id, ar.author_id, ar.title, ar.brief_introduction, ar.create_time, us.nick_name, us.avatar_path
FROM article ar, blog_info info, user_info us
WHERE ar.article_id = info.article_id AND us.author_id = ar.author_id AND ar.author_id = #{authorId}
LIMIT #{curPage},#{size}

SELECT count(article_id)
FROM article
WHERE author_id = #{authorId}
